mapper.select:
SELECT COUNT(*) OVER() AS ROW_CNT /*전체 레코드 수(페이징)*/ , T1.BOARD_SEQ /*게시글일련번호*/ , T1.TITLE /*제목*/ , T1.INTRO /*소개*/ , T1.CN /*상세내용*/ , T1.USE_YN /*사용여부*/ , T1.FST_REG_ID /*최초등록자아이디*/ , DATE_FORMAT(T1.FST_REG_DTTI, "%Y/%m/%d %H:%i") FST_REG_DTTI /*최초등록일시*/ , T1.LT_UPD_ID /*최종수정자아이디*/ , DATE_FORMAT(T1.LT_UPD_DTTI, "%Y/%m/%d %H:%i") LT_UPD_DTTI /*최종수정일시*/ FROM TB_PLAY T1 /*테이블_놀이*/ WHERE 1 = 1
AND T1.BOARD_SEQ = ${boardSeq}
AND T1.TITLE LIKE CONCAT('%',TRIM(#{title}),'%')
AND T1.USE_YN = #{useYn}
ORDER BY T1.FST_REG_DTTI DESC
ORDER BY T1.FST_REG_DTTI DESC
ORDER BY T1.TITLE ASC
LIMIT ${strIdx}, ${pageLength}

mapper.insert:

	SELECT nextval('BOARD_SEQ') FROM DUAL	/*boardSeq 값을 map에 반환*/

INSERT 
  INTO TB_PLAY					/*테이블_놀이*/
     (
       BOARD_SEQ                /*게시글일련번호*/ 
     , TITLE                    /*자유게시판제목*/ 
     , INTRO                    /*소개*/
     , CN                       /*자유게시판내용*/ 
     , FST_REG_ID 				/*최초등록자ID*/
     , LT_UPD_ID 				/*최종수정자ID*/
     )
VALUES
	 (
	   #{boardSeq}	
	 , #{title}	
	 , #{intro}
	 , #{cn}
     , #{loginInfo.userId} 	
     , #{loginInfo.userId}		 
	 )
