mapper.select:
SELECT COUNT(*) OVER() AS ROW_CNT /*전체 레코드 수(페이징)*/ , T1.USER_ID /*사용자아이디*/ , T1.USER_NAME /*사용자이름*/ , T1.SIGNUP_DT /*사용자가입일*/ , T1.USER_STATUS_CODE /*사용자상태코드*/ , (SELECT CODE_DETAIL_NM FROM TB_CODE_DETAIL WHERE CODE_GROUP = 'USER_STATUS_CODE' AND CODE_DETAIL = T1.USER_STATUS_CODE) AS USER_STATUS_CODE_NM /*사용자상태코드_한글명*/ , T1.USE_YN /*사용자사용여부*/ , T1.RMRK /*사용자비고*/ , T1.PW_CH_DTTI /*비밀번호최종수정일시*/ , T1.PW_BF /*이전 비밀번호*/ , T1.PW_ERR_CNT /*비밀번호 오입력 횟수*/ , T1.PW_INIT_YN /*비밀번호 초기화 여부*/ , T1.FST_REG_ID /*최초등록자아이디*/ , DATE_FORMAT(T1.FST_REG_DTTI, "%Y/%m/%d %H:%i") FST_REG_DTTI /*최초등록일시*/ , T1.LT_UPD_ID /*최종수정자아이디*/ , DATE_FORMAT(T1.LT_UPD_DTTI, "%Y/%m/%d %H:%i") LT_UPD_DTTI /*최종수정일시*/ , T2.ROLE_SEQ /*권한그룹일련번호*/ , T3.ROLE_NM /*권한그룹명*/ FROM TB_USER T1 /*테이블_사용자*/ , TB_USER_ROLE_MAP T2 /*테이블_사용자_권한그룹_매핑*/ , TB_ROLE T3 /*테이블_권한그룹*/ WHERE T1.USER_ID = T2.USER_ID AND T2.ROLE_SEQ = T3.ROLE_SEQ
AND T1.USER_ID = #{userId}
AND T1.USER_NAME LIKE CONCAT('%',TRIM(#{userName}),'%')
AND T2.ROLE_SEQ = #{roleSeq}
AND T1.USER_STATUS_CODE = #{userStatusCode}
ORDER BY T1.USER_ID ASC
ORDER BY T1.USER_NAME ASC
ORDER BY T1.FST_REG_DTTI DESC
LIMIT ${strIdx}, ${pageLength}

mapper.insert:
INSERT 
  INTO TB_USER					/*테이블_사용자*/
     (
       USER_ID					/*사용자아이디*/
     , USER_PW					/*사용자비밀번호*/
     , USER_NAME 				/*사용자이름*/
     , USER_STATUS_CODE			/*사용자상태코드*/
     , RMRK						/*사용자비고*/
     , FST_REG_ID 				/*최초등록자ID*/
     , LT_UPD_ID 				/*최종수정자ID*/
     )
VALUES
	 (
	   #{userId}	
	 , 'SXRLcrGA1f5nK8A5cvZICY86tW2d/Rekkm3lrWEgqJU='  	/*1*/
	 , #{userName}	
	 , #{userStatusCode}
	 , #{rmrkModal}
     , #{loginInfo.userId} 	
     , #{loginInfo.userId}		 
	 )

INSERT 
  INTO TB_USER_ROLE_MAP					/*테이블_사용자_권한그룹_매핑*/
     (
       USER_ID					/*사용자아이디*/
     , ROLE_SEQ 				/*권한그룹일련번호*/
     , FST_REG_ID 				/*최초등록자ID*/
     , LT_UPD_ID 				/*최종수정자ID*/
     )
VALUES
	 (
	   #{userId}	
	 , #{roleSeq}	
     , #{loginInfo.userId} 	
     , #{loginInfo.userId}		 
	 )
