mapper.select:
SELECT COUNT(*) OVER() AS ROW_CNT /*전체 레코드 수(페이징)*/ , T1.BOARD_SEQ /*게시글일련번호*/ , T1.TITLE /*자유게시판제목*/ , T1.CN /*자유게시판내용*/ , T1.BOARD_FREE_CODE /*자유게시판코드*/ , (SELECT CODE_DETAIL_NM FROM TB_CODE_DETAIL WHERE CODE_GROUP = 'BOARD_FREE_CODE' AND CODE_DETAIL = T1.BOARD_FREE_CODE) AS BOARD_FREE_CODE_NM /*자유게시판코드_한글명*/ , T1.USE_YN /*자유게시판사용여부*/ , T1.HIT /*자유게시판조회수*/ , T1.FST_REG_ID /*최초등록자아이디*/ , DATE_FORMAT(T1.FST_REG_DTTI, "%Y/%m/%d %H:%i") FST_REG_DTTI /*최초등록일시*/ , T1.LT_UPD_ID /*최종수정자아이디*/ , DATE_FORMAT(T1.LT_UPD_DTTI, "%Y/%m/%d %H:%i") LT_UPD_DTTI /*최종수정일시*/ FROM TB_BOARD_FREE T1 /*테이블_자유게시판*/ WHERE 1 = 1
AND T1.BOARD_SEQ = ${boardSeq}
AND T1.TITLE LIKE CONCAT('%',TRIM(#{title}),'%')
AND T1.FST_REG_ID LIKE CONCAT('%',TRIM(#{userId}),'%')
AND FST_REG_DTTI BETWEEN #{dttiStr} AND DATE_ADD(#{dttiEnd}, INTERVAL 1 DAY)
AND T1.BOARD_FREE_CODE = #{boardFreeCode}
AND T1.USE_YN = #{useYn}
ORDER BY T1.LT_UPD_DTTI DESC
LIMIT ${strIdx}, ${pageLength}

mapper.insert:

	SELECT nextval('BOARD_SEQ') FROM DUAL	/*boardSeq 값을 map에 반환*/

INSERT 
  INTO TB_BOARD_FREE			/*테이블_자유게시판*/
     (
       BOARD_SEQ                /*게시글일련번호*/ 
     , TITLE                    /*자유게시판제목*/ 
     , CN                       /*자유게시판내용*/ 
     , BOARD_FREE_CODE          /*자유게시판코드(01:잡담,02:정보,03:질문)*/ 
     , FST_REG_ID 				/*최초등록자ID*/
     , LT_UPD_ID 				/*최종수정자ID*/
     )
VALUES
	 (
	   #{boardSeq}	
	 , #{title}	
	 , #{cn}
	 , #{boardFreeCode}
     , #{loginInfo.userId} 	
     , #{loginInfo.userId}		 
	 )
