mapper.select:
SELECT T1.CHAL_SEQ /*챌린지일련번호*/ , T1.WORDBOOK_SEQ /*단어장일련번호*/ , T1.WORD_ID /*단어식별자*/ , T1.SUCCESS_YN /*정답여부*/ , T1.FST_REG_ID /*최초등록자아이디*/ , DATE_FORMAT(T1.FST_REG_DTTI, "%Y/%m/%d %H:%i") FST_REG_DTTI /*최초등록일시*/ , T1.LT_UPD_ID /*최종수정자아이디*/ , DATE_FORMAT(T1.LT_UPD_DTTI, "%Y/%m/%d %H:%i") LT_UPD_DTTI /*최종수정일시*/ , T2.WORD /*단어*/ , T2.MEAN /*뜻*/ FROM TB_CHAL_WORD T1 /*테이블_챌린지_단어*/ , TB_WORD T2 /*테이블_단어*/ WHERE 1 = 1 AND T1.WORDBOOK_SEQ = T2.WORDBOOK_SEQ AND T1.WORD_ID = T2.WORD_ID
AND T1.CHAL_SEQ = #{chalSeq}
AND T1.SUCCESS_YN = #{successYn}
ORDER BY RAND()

SELECT T1.CHAL_SEQ /*챌린지일련번호*/ , T1.CHAL_NM /*챌린지명*/ , T1.USE_YN /*사용여부*/ , T1.FST_REG_ID /*최초등록자아이디*/ , DATE_FORMAT(T1.FST_REG_DTTI, "%Y/%m/%d %H:%i") FST_REG_DTTI /*최초등록일시*/ , T1.LT_UPD_ID /*최종수정자아이디*/ , DATE_FORMAT(T1.LT_UPD_DTTI, "%Y/%m/%d %H:%i") LT_UPD_DTTI /*최종수정일시*/ FROM TB_CHAL T1 /*테이블_챌린지*/ WHERE 1 = 1
AND T1.USE_YN = #{useYn}
ORDER BY CHAL_NM ASC
