mapper.select:
SELECT COUNT(*) OVER() AS ROW_CNT /*전체 레코드 수(페이징)*/ , T1.BOARD_SEQ /*게시글일련번호*/ , T1.TITLE /*공지사항제목*/ , T1.CN /*공지사항내용*/ , T1.STR_DT /*공지사항게시시작일*/ , T1.END_DT /*공지사항게시종료일*/ , CONCAT(DATE_FORMAT(T1.STR_DT, "%Y-%m-%d"), " ~ ", DATE_FORMAT(T1.END_DT, "%Y-%m-%d")) AS PERIOD /*게시기간*/ , T1.POP_YN /*공지사항팝업여부*/ , T1.CN /*공지사항내용*/ , T1.HIT /*공지사항조회수*/ , T1.FST_REG_ID /*최초등록자ID*/ , DATE_FORMAT(T1.FST_REG_DTTI, "%Y/%m/%d %H:%i") FST_REG_DTTI /*최초등록일시*/ , T1.LT_UPD_ID /*최종수정자ID*/ , DATE_FORMAT(T1.LT_UPD_DTTI, "%Y/%m/%d %H:%i") LT_UPD_DTTI /*최종수정일시*/ FROM TB_BOARD_NOTICE T1 /*테이블_공지사항*/ WHERE 1 = 1
AND DATE_FORMAT(NOW(), "%Y%m%d") BETWEEN T1.STR_DT AND T1.END_DT
AND T1.BOARD_SEQ = ${boardSeq}
AND T1.POP_YN = #{popYn}
ORDER BY T1.STR_DT DESC
LIMIT ${strIdx}, ${pageLength}

mapper.insert:

	SELECT nextval('BOARD_SEQ') FROM DUAL	/*boardSeq 값을 map에 반환*/

INSERT 
  INTO TB_BOARD_NOTICE			/*테이블_공지사항*/
     (
       BOARD_SEQ				/*게시글일련번호*/
     , TITLE 					/*공지사항제목*/
     , CN						/*공지사항내용*/
     , STR_DT					/*공지사항게시시작일*/
     , END_DT					/*공지사항게시종료일*/
     , POP_YN					/*공지사항팝업여부*/
     , FST_REG_ID 				/*최초등록자ID*/
     , LT_UPD_ID 				/*최종수정자ID*/
     )
VALUES
	 (
	   #{boardSeq}	
	 , #{title}	
	 , #{cn}
	 , #{strDt}
	 , #{endDt}
	 , #{popYn}
     , #{loginInfo.userId} 	
     , #{loginInfo.userId}		 
	 )
